package com.lcvc.ebuy_maven_ssm.web.backstage;import com.lcvc.ebuy_maven_ssm.dao.AdminDao;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpSession;@Controllerpublic class LoginLoginoutController {	AdminDao adminDao=new AdminDao();	@RequestMapping(value = "/backstage/login", method = RequestMethod.POST)	public String login(String username,String password ,HttpSession session) {		if(adminDao.login(username, password)){			session.setAttribute("username",username);			return "redirect:/backstage/index";		}else{			return "/jsp/backstage/login.jsp";		}	}	@RequestMapping(value = "/backstage/index")	public String index() {		return "/jsp/backstage/main.jsp";	}}